import java.lang.String;import java.security.PrivateKey;import xri.at_mcubed.polydata.Client;public class SampleUsage{	public static void main(String[] args) throws Exception	{     System.out.println("SampleUsage of Polydata ... ");    System.out.println("");    System.out.println("Get a new Polydata Client Object");    System.out.println("CODE: Client polydata = new Client();");	  Client polydata = new Client();    System.out.println("");    System.out.println("Get the Private Key");    System.out.println("  args are: " + args[0] + ", " + args[1] + ", " + args[2] + ", " + args[3] );    System.out.println("CODE: PrivateKey privateKey = polydata.get_private_key( args );");	  PrivateKey privateKey = polydata.get_private_key( args );    System.out.println("");    System.out.println("Get an auth_token");    System.out.println("CODE: String auth_token = polydata.get_auth_token(privateKey);");		String auth_token = polydata.get_auth_token(privateKey);    System.out.println("auth_token = " + auth_token );    System.out.println("");    System.out.println("Test the auth_token at http://data.llli.org/guide/verify_encrypted");    System.out.println("");    System.out.println("** Alternate method to get auth_token, get an explicit timestamp **");    System.out.println("Get the timestamp");    System.out.println("CODE: String timestamp = polydata.get_secs_now();");		String timestamp = polydata.get_secs_now();    System.out.println("timestamp " + timestamp );    System.out.println("Verify the timestamp = http://data.llli.org/guide/verify_timestamp/" + timestamp );    System.out.println("");    System.out.println("Get the auth_token with the timestamp: " + timestamp);    System.out.println("CODE: String auth_token = polydata.get_auth_token(privateKey, timestamp);");		auth_token = polydata.get_auth_token(privateKey, timestamp);    System.out.println("auth_token = " + auth_token );    System.out.println("");    System.out.println("Test the auth_token at http://data.llli.org/guide/verify_encrypted");    System.out.println("");    System.out.println("** End Alternate method **");    System.out.println("End SampleUsage of Polydata");	}}